		    ,FLY
		    ,XZKD
		    ,XZZY
        ) VALUES (
		    	#{patientCode}
		    	,#{mSCB}
		    	,#{mSJB}
		    	,#{mSAH}
		    	,#{mSWH}
		    	,#{mH}
		    	,#{mC}
		    	,#{yZSA}
		    	,#{yJFZ}
		    	,#{cYAH}
		    	,#{cJSD}
		    	,#{eNMA}
